const UserForm = () => {
    const titleChangeHandler = (event)=>{
        console.log(event.target.value)
    }
  return (
    <form>
      <div>
        <div>
          <label>Text</label>
          <input type="text" onChange={titleChangeHandler} />
        </div>
        <div>
          <label>Number</label>
          <input type="date"  />
        </div>
        <div>
          <label>Merhaba</label>
          <input type="number" />
        </div>
        <button type = "submit">Submit</button>
      </div>
    </form>
  );
};

export default UserForm;
